sys_script_include.script:
// PropertySearchAjaxProcessor script include

var PropertySearchAjaxProcessor = Class.create();

PropertySearchAjaxProcessor.prototype = Object.extendsObject(AbstractAjaxProcessor, {

   searchProperty: function() {
      var price = this.getParameter('sysparm_price');
      var number = this.getParameter('sysparm_number');
      var postalCode = this.getParameter('sysparm_postal_code');

      // Add your server-side search logic here
      // Example: Perform a GlideRecord query and log the results
      var gr = new GlideRecord('x_1129837_happyimm_property_info');
      if (price)
          gr.addQuery('price', price);
      if (number)
          gr.addQuery('number', number);
      if (postalCode)
          gr.addQuery('postal_code', postalCode);
      gr.query();

      // Process the search results
      while (gr.next()) {
         gs.info('Found matching record with price: ' + gr.getValue('price') +
                 ', number: ' + gr.getValue('number') +
                 ', postal code: ' + gr.getValue('postal_code'));
      }

      // Return success or failure based on your business logic
      return 'success';
   }
});
